package dk.sdu.mmmi.cbse.scoringsystem;import org.springframework.boot.SpringApplication;import org.springframework.boot.autoconfigure.SpringBootApplication;import org.springframework.web.bind.annotation.GetMapping;import org.springframework.web.bind.annotation.PostMapping;import org.springframework.web.bind.annotation.RequestBody;import org.springframework.web.bind.annotation.RestController;@SpringBootApplication@RestControllerpublic class ScoringSystem {    private int currentScore = 0;    public static void main(String[] args) {        System.out.println("Scoring System is running");        SpringApplication.run(ScoringSystem.class, args);    }    @GetMapping("/score")    public int getScore() {        return currentScore;    }    @PostMapping("/score/increment")    public int incrementScore(@RequestBody ScoreDTO scoreDTO) {        currentScore += scoreDTO.getScore();        return currentScore;    }}